import React from 'react';
import Recipe from './Recipe';

export default function RecipeList({ recipes }) {
  return (
    <div>
      {recipes.map((recipe) => {
        return <Recipe key={recipe.id} {...recipe} />;
      })}
    </div>
  );
}

/* spread operator (...) is going to take all of the different key value pairs of 'recipe' and put them as an individual attribute of the 'recipe'
It allows to pass down all of our properties as top levels of the prop instead of being nested inside the recipe itself */
